let color = ['#5B8FF988']
function series() {
    const s = []
    for (var i = 0; i < dataset.dimensions.length - 1; i++) {
        const dim = dataset.dimensions[(i + 1)]
        s.push({
            type: 'scatter',
            symbolSize: function (data) {
                return data['平均时长'] * 10
                const size = Math.sqrt(data[dim])
                let retSize = 0
                if (size < 5000) {
                    retSize = size / 2
                } else {
                    retSize = size / 5e2
                }
                //  控制散点大小
                return retSize >= 80 ? 80 : retSize < 10 ? retSize + 5 : retSize
            },
            // symbolSize:data[dim],
            emphasis: {
                label: {
                    show: false,
                    position: 'bottom'
                }
            },
            itemStyle: {
                color: color[i],
                // borderColor:borderColor[i]
            }
        });

    }
    return s
}
